// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;

/// @title 
contract Ballot {
    // 
    struct Voter {
        uint256 weight; //
        bool voted; //
        address delegate; //
        uint256 vote; //
    }
    // 
    struct Proposal {
        bytes32 name; //32
        uint256 voteCount; //
    }
    address public chairperson; //

    //'Voter';
    mapping(address => Voter) public voters;

    //'Proposal' ;
    Proposal[] public proposals;

    // storagememory
    // 'ProposalNames'
    constructor(bytes32[] memory proposalsNames) {
        //msg.sender;
        // msg.sendermsg.sender
        chairperson = msg.sender;
        voters[chairperson].weight = 1;
        // 
        //  Proposal 
        for (uint256 i = 0; i < proposalsNames.length; i++) {
            proposals.push(Proposal({name: proposalsNames[i], voteCount: 0}));
        }
    }

    // 'Voter'
    // 'chairperson'
    function giveRightToVote(address voter) public {
        // 'require''false'
        // 
        // EVMgas
        // require
        // require
        require(
            msg.sender == chairperson,
            "Only chairperson can give right to vote."
        );
        require(!voters[voter].voted, "The voter already voted.");
        require(voters[voter].weight == 0);
        voters[voter].weight = 1;
    }

    // 'to',
    function delegate(address to) public {
        Voter storage sender = voters[msg.sender];
        require(!sender.voted, "You already voted.");
        require(
            to != msg.sender,
            // 
            "Found loop in delegation."
        );

        sender.voted = true; //
        sender.delegate = to;
        Voter storage delegate_ = voters[to]; //
        if (delegate_.voted) {
            // 
            proposals[delegate_.vote].voteCount += sender.weight;
        } else {
            // 
            delegate_.weight += sender.weight;
        }
    }

    // 'proposals[proposal].name'
    // 
    function vote(uint256 proposal) public {
        Voter storage sender = voters[msg.sender];
        require(!sender.voted, "Already voted.");
        sender.voted = true;
        sender.vote = proposal; //proposal
        // proposal;
        proposals[proposal].voteCount += sender.weight;
    }

    // 
    function weinningProposal() public view returns (uint256 winningProposal_) {
        uint256 winningVoteCount = 0;
        for (uint256 p = 0; p < proposals.length; p++) {
            if (proposals[p].voteCount > winningVoteCount) {
                winningVoteCount = proposals[p].voteCount;
                winningProposal_ = p;
            }
        }
    }

    // 
    function winnerName() public view returns (bytes32 winnerName_) {
        winnerName_ = proposals[weinningProposal()].name;
    }
}
